input +.terms-showMore {
	font-size: 16px;
	display: block;
	cursor: pointer;
	padding: 10px 0;
	margin: 10px 0;
	color: #174567;
	font-weight: 600;
	background-image: url(show.png);
	background-repeat: no-repeat;
	background-position: 100% 50%;
}

input:checked + .terms-showMore {
    background-image: url(hide.png);
}


input {
	display:none;
}
	input + .terms-showMore +  * {
	font-size:15px;
	padding-left:5px;
	text-align:left;
	color:#666666;
	max-height: 0;
	/*and eventually delay an overflow:auto; */
	overflow:hidden;
	transition: max-height 1s, overflow 1s;
}
input:checked + .terms-showMore  + * { 
	/* here comes the compromise, set a max-height that would for your usual contents*/
	max-height: 2000px;
	overflow:auto;
	transition: max-height 1s, overflow 1s;
}



