components/products/ratings:
{{#for 1 5}}
    {{#if ../this.rating '>=' $index}}
        <span class="icon icon--ratingFull">
            <svg>
                <use xlink:href="#icon-star" />
            </svg>
        </span>
    {{else}}
        <span class="icon icon--ratingEmpty">
            <svg>
                <use xlink:href="#icon-star" />
            </svg>
        </span>
    {{/if}}
{{/for}}
{{{snippet 'product_rating'}}}

components/products/reviews:
{{#if reviews.total}}
<section class="productReviews" id="product-reviews" data-product-reviews>
    <h2 class="page-heading">
        <span>{{lang 'products.reviews.header' total=reviews.total}}</span>
    </h4>
    <div class="productReviews-content" id="productReviews-content">
        <ul class="productReviews-list" id="productReviews-list">
            {{#each reviews.list}}
            <li class="productReview"{{> components/f/animations/aos delay=(multiply (add @index 1) 50) once=true theme_settings=../../theme_settings}}>
                <article itemprop="review" itemscope itemtype="http://schema.org/Review">
                    <header>
                        <span class="productReview-rating rating--small" itemprop="reviewRating" itemscope itemtype="http://schema.org/Rating">
                            {{> components/products/ratings rating=rating}}
                            <span class="productReview-ratingNumber" itemprop="ratingValue">{{ rating }}</span>
                        </span>
                        <h5 itemprop="name" class="productReview-title">{{ title }}</h5>
                        {{#if name}}
                            <meta itemprop="author" content="{{name}}">
                            <p class="productReview-author">
                                {{lang 'products.reviews.post_on_by' date=date name=name }}
                            </p>
                        {{else}}
                            <p class="productReview-author">
                                {{lang 'products.reviews.post_on_by' date=date name=(lang 'products.reviews.anonymous_poster')}}
                            </p>
                        {{/if}}
                    </header>
                    <p itemprop="reviewBody" class="productReview-body">{{nl2br text}}</p>
                </article>
            </li>
            {{/each}}
        </ul>
        {{> components/common/paginator pagination.reviews}}
    </div>
</section>
{{/if}}
{{{snippet 'reviews'}}}

components/products/upsell:
{{#if product.related_products}}
    <section class="content productView-upsell">
        <h2 class="page-heading"><span>{{lang 'products.related_products'}}</span></h2>
        {{> components/products/carousel products=product.related_products columns=6 show_wishlist=settings.show_wishlist show_rating=settings.show_product_rating}}
    </section>
{{/if}}

{{#if product.similar_by_views}}
    <section class="content productView-upsell">
        <h2 class="page-heading"><span>{{lang 'products.similar_by_views'}}</span></h2>
        {{> components/products/carousel products=product.similar_by_views columns=6 show_wishlist=settings.show_wishlist show_rating=settings.show_product_rating}}
    </section>
{{/if}}

pages/custom/product/product-cool:
---
product:
    videos:
        limit: {{theme_settings.productpage_videos_count}}
    reviews:
        limit: {{theme_settings.productpage_reviews_count}}
    related_products:
        limit: {{theme_settings.productpage_related_products_count}}
    similar_by_views:
        limit: {{theme_settings.productpage_similar_by_views_count}}
---
{{inject "enableSwatches" theme_settings.show_swatches_on_grid}}
{{inject "enableSizes" theme_settings.show_sizes_on_grid}}
{{inject "enableImageHoverSwitcher" theme_settings.enable_image_hover_switcher}}
{{inject "enableSelectedOptionLabel" theme_settings.show_selected_option_on_label}}
{{inject "addToCartMode" theme_settings.add_to_cart_mode}}
{{#partial "page"}}
    

    <div itemscope itemtype="http://schema.org/Product">
        {{> components/products/custom-product-cool settings=../settings schema=true}}

        {{#if product.videos.list.length}}
            {{> components/products/videos product.videos}}
        {{/if}}

        {{#if theme_settings.enable_editorial_mode}}
            {{> components/f/products/related-article}}
        {{/if}}

        <!--{{#all settings.show_product_reviews product.reviews.total}}
            {{> components/products/reviews reviews=product.reviews product=product urls=urls}}
        {{/all}}-->

        <!--{{#if theme_settings.show_product_details_tabs}}
            {{#if theme_settings.conversion_optimization_mode}}
                {{> components/products/upsell}}
            {{else}}
                {{> components/products/tabs}}
            {{/if}}
        {{else}}
            {{> components/products/upsell}}
        {{/if}}-->

    </div>

{{/partial}}
