components/amp/css/base:
*,
*:after,
*:before {
    box-sizing: border-box;
}
html {
    font-size: 14px
}
html,
body {
    height: auto
}

body {
    font-size: 1rem;
    font-family: "{{last (first (split theme_settings.body-font '_') 2)}}", Arial, Helvetica, sans-serif;
    font-weight: 400;
    line-height: 1.5;
    color: {{theme_settings.color-textBase}};
    padding-top: 55px;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}
ul {
    margin: 0;
    padding: 0;
}
li {
    padding: 0;
    margin: 0;
}
a {
    color: {{theme_settings.color-textLink}};
    line-height: inherit;
    text-decoration: underline;
}
a:hover,
a:focus {
    color: {{theme_settings.color-textLink--hover}}
}
p {
    margin-bottom: 1.5rem
}

h1,
h2,
h3,
h4,
h5,
h6 {
    color: {{theme_settings.color-textHeading}};
    font-family: "{{last (first (split theme_settings.headings-font '_') 2)}}", Arial, Helvetica, sans-serif;
    font-weight: 400;
    letter-spacing: .25px;
    margin-bottom: .78571rem;
    margin-top: 2.28571rem;
    text-transform: uppercase;
}
h1 small,
h2 small,
h3 small,
h4 small,
h5 small,
h6 small {
    color: {{theme_settings.color-textSecondary}};
    font-size: 60%;
    line-height: 0
}
h1 {
    font-size: 28px
}
h2 {
    font-size: 25px
}
h3 {
    font-size: 22px
}
h4 {
    font-size: 20px
}
h5 {
    font-size: 15px
}
h6 {
    font-size: 13px;
    text-transform: uppercase
}

hr {
    border: solid {{theme_settings.container-border-global-color-base}};
    border-width: 1px 0 0;
    clear: both;
    height: 0;
    margin: 2rem 0 1.92857rem
}
em,
i {
    font-style: italic;
    line-height: inherit
}
strong,
b {
    font-weight: 700;
    line-height: inherit
}
small {
    font-size: 60%;
    line-height: inherit
}
ul,
ol,
dl {
    list-style-position: outside;
    margin-bottom: 1.5rem
}
ul ul,
ul ol,
ol ul,
ol ol {
    margin-left: 1.2rem;
    margin-bottom: 0
}
ul {
    margin-left: 1.15rem
}
ol {
    margin-left: 1.15rem
}
dl dt {
    font-weight: 700;
}
abbr,
acronym {
    border-bottom: 1px dotted {{theme_settings.container-border-global-color-base}};
    color: {{theme_settings.color-textBase}};
    cursor: help;
    font-size: 90%;
    text-transform: uppercase
}
abbr {
    text-transform: none
}
blockquote,
blockquote p {
    font-size: 22px;
    line-height: 29px
}
cite {
    color: {{theme_settings.color-primary}};
    display: block;
    font-size: 1rem;
    line-height: 1.5;
}
.icon {
    height: 1.14286rem;
    width: 1.14286rem;
    display: inline-block;
    vertical-align: middle
}
.icon svg {
    display: inline-block;
    fill: {{theme_settings.icon-color}};
    height: 100%;
    vertical-align: top;
    width: 100%
}
.is-srOnly,
.modal {
    display: none;
}

.body {
    margin-bottom: 6rem;
    margin-top: 1.5rem
}

.container {
    margin-left: auto;
    margin-right: auto;
    max-width: 91.71429rem;
    padding: 0 1.5rem;
    position: relative;
}

.button {
    border-radius: 0;
    border-style: solid;
    border-width: 1px;
    cursor: pointer;
    font-family: "{{last (first (split theme_settings.body-font '_') 2)}}", Arial, Helvetica, sans-serif;
    font-weight: 400;
    line-height: normal;
    margin: 0 0 1rem;
    position: relative;
    text-align: center;
    text-decoration: none;
    display: inline-block;
    background-color: transparent;
    border-color: {{theme_settings.button--default-borderColor}};
    color: {{theme_settings.button--default-color}};
    font-size: 1rem;
    padding: .85714rem 2.28571rem;
    border-radius: 4px;
    outline: none;
    vertical-align: middle
}
.button:hover,
.button:focus,
.button.is-active,
.button.active {
    background-color: transparent;
    border-color: {{theme_settings.default-borderColorHover}};
    color: {{theme_settings.default-colorHover}};
}
.button:active {
    background-color: transparent;
    border: 1px solid {{theme_settings.default-borderColorActive}};
    color: {{theme_settings.default-colorActive}};
}
.button:focus,
.button.is-active,
.button.active {
    outline: none
}
.button--primary {
    background-color: {{theme_settings.button--primary-backgroundColor}};
    border-color: {{theme_settings.button--primary-backgroundColor}};
    color: {{theme_settings.button--primary-color}};
}
.button--primary:hover,
.button--primary:focus,
.button--primary.is-active,
.button--primary.active {
    background-color: {{theme_settings.button--primary-backgroundColorHover}};
    border-color: {{theme_settings.button--primary-backgroundColorHover}};
    color: {{theme_settings.button--primary-colorHover}};
}
.button--primary:active {
    background-color: {{theme_settings.button--primary-backgroundColorActive}};
    border: 1px solid {{theme_settings.button--primary-backgroundColorActive}};
    color: {{theme_settings.button--primary-colorActive}};
}
.button[disabled] {
    background-color: {{theme_settings.disabled-backgroundColor}};
    border-color: {{theme_settings.disabled-borderColor}};
    color: {{theme_settings.button--disabled-color}};
    cursor: default
}

.button--wide {
    display: block;
    width: 100%
}

.button--icon {
    font-size: 1rem;
    padding: .78571rem
}
.button--icon svg {
    fill: {{theme_settings.button--primary-color}};
}

.icon--ratingEmpty svg {
    fill: {{theme_settings.icon-ratingEmpty}};
}

.icon--ratingFull svg {
    fill: {{theme_settings.icon-ratingFull}};
}

.price--rrp,
.price--non-sale,
.price--discounted {
    text-decoration: line-through;
}

.navPages li {
    list-style-type: none;
}



amp-img img {
    object-fit: contain;
}
