components/amp/css/base:
*,
*:after,
*:before {
    box-sizing: border-box;
}
html {
    font-size: 14px
}
html,
body {
    height: auto
}

body {
    font-size: 1rem;
    font-family: "{{last (first (split theme_settings.body-font '_') 2)}}", Arial, Helvetica, sans-serif;
    font-weight: 400;
    line-height: 1.5;
    color: {{theme_settings.color-textBase}};
    padding-top: 55px;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}
ul {
    margin: 0;
    padding: 0;
}
li {
    padding: 0;
    margin: 0;
}
a {
    color: {{theme_settings.color-textLink}};
    line-height: inherit;
    text-decoration: underline;
}
a:hover,
a:focus {
    color: {{theme_settings.color-textLink--hover}}
}
p {
    margin-bottom: 1.5rem
}

h1,
h2,
h3,
h4,
h5,
h6 {
    color: {{theme_settings.color-textHeading}};
    font-family: "{{last (first (split theme_settings.headings-font '_') 2)}}", Arial, Helvetica, sans-serif;
    font-weight: 400;
    letter-spacing: .25px;
    margin-bottom: .78571rem;
    margin-top: 2.28571rem;
    text-transform: uppercase;
}
h1 small,
h2 small,
h3 small,
h4 small,
h5 small,
h6 small {
    color: {{theme_settings.color-textSecondary}};
    font-size: 60%;
    line-height: 0
}
h1 {
    font-size: 28px
}
h2 {
    font-size: 25px
}
h3 {
    font-size: 22px
}
h4 {
    font-size: 20px
}
h5 {
    font-size: 15px
}
h6 {
    font-size: 13px;
    text-transform: uppercase
}

hr {
    border: solid {{theme_settings.container-border-global-color-base}};
    border-width: 1px 0 0;
    clear: both;
    height: 0;
    margin: 2rem 0 1.92857rem
}
em,
i {
    font-style: italic;
    line-height: inherit
}
strong,
b {
    font-weight: 700;
    line-height: inherit
}
small {
    font-size: 60%;
    line-height: inherit
}
ul,
ol,
dl {
    list-style-position: outside;
    margin-bottom: 1.5rem
}
ul ul,
ul ol,
ol ul,
ol ol {
    margin-left: 1.2rem;
    margin-bottom: 0
}
ul {
    margin-left: 1.15rem
}
ol {
    margin-left: 1.15rem
}
dl dt {
    font-weight: 700;
}
abbr,
acronym {
    border-bottom: 1px dotted {{theme_settings.container-border-global-color-base}};
    color: {{theme_settings.color-textBase}};
    cursor: help;
    font-size: 90%;
    text-transform: uppercase
}
abbr {
    text-transform: none
}
blockquote,
blockquote p {
    font-size: 22px;
    line-height: 29px
}
cite {
    color: {{theme_settings.color-primary}};
    display: block;
    font-size: 1rem;
    line-height: 1.5;
}
.icon {
    height: 1.14286rem;
    width: 1.14286rem;
    display: inline-block;
    vertical-align: middle
}
.icon svg {
    display: inline-block;
    fill: {{theme_settings.icon-color}};
    height: 100%;
    vertical-align: top;
    width: 100%
}
.is-srOnly,
.modal {
    display: none;
}

.body {
    margin-bottom: 6rem;
    margin-top: 1.5rem
}

.container {
    margin-left: auto;
    margin-right: auto;
    max-width: 91.71429rem;
    padding: 0 1.5rem;
    position: relative;
}

.button {
    border-radius: 0;
    border-style: solid;
    border-width: 1px;
    cursor: pointer;
    font-family: "{{last (first (split theme_settings.body-font '_') 2)}}", Arial, Helvetica, sans-serif;
    font-weight: 400;
    line-height: normal;
    margin: 0 0 1rem;
    position: relative;
    text-align: center;
    text-decoration: none;
    display: inline-block;
    background-color: transparent;
    border-color: {{theme_settings.button--default-borderColor}};
    color: {{theme_settings.button--default-color}};
    font-size: 1rem;
    padding: .85714rem 2.28571rem;
    border-radius: 4px;
    outline: none;
    vertical-align: middle
}
.button:hover,
.button:focus,
.button.is-active,
.button.active {
    background-color: transparent;
    border-color: {{theme_settings.default-borderColorHover}};
    color: {{theme_settings.default-colorHover}};
}
.button:active {
    background-color: transparent;
    border: 1px solid {{theme_settings.default-borderColorActive}};
    color: {{theme_settings.default-colorActive}};
}
.button:focus,
.button.is-active,
.button.active {
    outline: none
}
.button--primary {
    background-color: {{theme_settings.button--primary-backgroundColor}};
    border-color: {{theme_settings.button--primary-backgroundColor}};
    color: {{theme_settings.button--primary-color}};
}
.button--primary:hover,
.button--primary:focus,
.button--primary.is-active,
.button--primary.active {
    background-color: {{theme_settings.button--primary-backgroundColorHover}};
    border-color: {{theme_settings.button--primary-backgroundColorHover}};
    color: {{theme_settings.button--primary-colorHover}};
}
.button--primary:active {
    background-color: {{theme_settings.button--primary-backgroundColorActive}};
    border: 1px solid {{theme_settings.button--primary-backgroundColorActive}};
    color: {{theme_settings.button--primary-colorActive}};
}
.button[disabled] {
    background-color: {{theme_settings.disabled-backgroundColor}};
    border-color: {{theme_settings.disabled-borderColor}};
    color: {{theme_settings.button--disabled-color}};
    cursor: default
}

.button--wide {
    display: block;
    width: 100%
}

.button--icon {
    font-size: 1rem;
    padding: .78571rem
}
.button--icon svg {
    fill: {{theme_settings.button--primary-color}};
}

.icon--ratingEmpty svg {
    fill: {{theme_settings.icon-ratingEmpty}};
}

.icon--ratingFull svg {
    fill: {{theme_settings.icon-ratingFull}};
}

.price--rrp,
.price--non-sale,
.price--discounted {
    text-decoration: line-through;
}

.navPages li {
    list-style-type: none;
}



amp-img img {
    object-fit: contain;
}

components/amp/css/category:
.container {
    margin-left: auto;
    margin-right: auto;
    max-width: 91.71429rem;
    padding: .5rem 1.5rem;
}

.categoryView-img {
    padding: 0 0 1rem;
}

.categoryView-title {
    font-size: 20px;
    margin: 7px 0;
    text-align: center;
    text-transform: uppercase;
}

.page-content p {
    text-align: center;
}

.sidebarBlock {
    margin-left: auto;
    margin-right: auto;
    max-width: 91.71429rem;
    padding: 1rem 1.5rem;
    background-color: {{theme_settings.navPages-subMenu-backgroundColor}};
    text-align: center;
    border-top: 1px solid {{theme_settings.navPages-subMenu-separatorColor}};
}

.sidebarBlock-heading {
    margin: .78571rem 0;
}

.navList {
    list-style: none;
    padding: 0;
    margin-left: 0;
}

.navList-action {
    color: {{theme_settings.navPages-subMenu-color}};
    font-size: 15px;
    padding: 3px 0;
    display: block;
}

.actionBar-section .form-field {
    background-color: {{theme_settings.input-bg-color}};
    border: 1px solid {{theme_settings.input-border-color}};
    border-radius: 4px;
    overflow: hidden;
    display: inline-block;
}

.actionBar-section .form-label {
    color: {{theme_settings.form-label-font-color}};
    font-size: 13px;
    line-height: 2.14286rem;
    margin-bottom: 0;
    padding-left: 0.78571rem;
    display: inline-block;
}

.actionBar-section .form-select {
    color: {{theme_settings.input-font-color}};
    height: 2.14286rem;
    display: inline-block;
}

.productGrid {
    display: flex;
    flex-wrap: wrap;
    list-style: none;
    margin: 0;
    padding: 0;
}

.productGrid .product {
    width: 50%;
    padding: 1rem 1.5rem;
}

.productList {
    list-style: none;
    margin: 0;
    padding: 0;
}

.productList .product {
    padding: 1rem 1.5rem;
}

.listItem-rating,
.listItem-brand,
.listItem-title {
    margin: .5rem 0;
}

.listItem-title {
    font-size: 15px;
}

.listItem-price {
    margin-bottom: 1rem;
}


.card-figure {
    margin: 0;
    padding: 0;
    position: relative;
}

.card-image {
    max-width: 100%;
    height: auto;
    margin-bottom: 10px;
}

.card-body {
    text-align: center;
}

.card-text,
.card-title {
    margin: 5px 0;
}

.card-text {
    color: {{theme_settings.card-title-color}};
    font-size: 14px;
    font-weight: normal;
}

.card-title {
    font-size: 15px;
}

.card-title a {
    text-decoration: none;
}

.rating--small {
    display: block;
    margin-bottom: 10px;
}

.sale-sash {
    background: {{theme_settings.sale_tag_backgroundColor}};
    color: {{theme_settings.sale_tag_text_color}};
    font-size: 15px;
    padding: 1px 5px;
    position: absolute;
    text-align: center;
    bottom: 0;
    width: 100%;
    z-index: 10;
}

.pagination {
    margin: 2rem 1.5rem 2.28571rem;
}

.pagination-list {
    display: block;
    margin: 0;
    text-align: center;
    list-style: none;
    padding: 0 80px;
}

.pagination-item {
    display: inline-block;
    border: 1px solid transparent;
    border-radius: 4px;
    font-size: 15px;
    position: relative;
}

.pagination-link {
    display: block;
    padding: 3px 10px;
}

.pagination-item--current {
    border-color: {{theme_settings.container-border-global-color-base}};
}

.pagination-item--previous {
    position: absolute;
    left: 1rem;
}

.pagination-item--next {
    position: absolute;
    right: 1rem;
}

components/amp/css/footer:
.amp-footer {
    background-color: {{theme_settings.footer-backgroundColor}};
    color: {{theme_settings.footer-textColor}};
    text-align: center;
    border-top: 1px solid {{theme_settings.container-border-global-color-base}};
}

.amp-footer a {
    color: {{theme_settings.footer-linkColor}};
    text-decoration: none
}

.amp-footer a:hover {
    color: {{theme_settings.footer-linkColorHover}};
}

.amp-footer-list {
    margin: 0;
    list-style-type: none;
}

.amp-footer-list li {
    padding: 3px 0;
}

address {
    font-style: normal;
}

article {
    overflow: hidden;
}

components/amp/css/header:
.amp-menu-button {
    background: transparent;
    border: 0;
    cursor: pointer;
    width: 3.92857rem;
    height: 3.92857rem;
    display: inline-block;
    font-size: 0;
    left: 0;
    overflow: hidden;
    padding: 0 1.1rem;
    position: absolute;
    z-index: 50;
}

.amp-menu-button-toggle,
.amp-menu-button-toggle::before,
.amp-menu-button-toggle::after {
    background: #050505;
    content: "";
    display: block;
    height: 2px;
    position: absolute;
    transform: rotate(0deg);
    transition: transform 100ms ease-in, top 100ms ease-in 150ms, bottom 100ms ease-in 150ms;
    width: 1.66667rem;
}

.amp-menu-button-toggle::before {
    top: -0.5rem;
}

.amp-menu-button-toggle::after {
    bottom: -0.5rem;
}

.amp-menu-button-toggle {
    top: 1.8rem;
    transition: background-color 10ms ease-in 100ms;
}

.amp-header {
    background-color: {{theme_settings.header-backgroundColor}};
    border-bottom: 1px solid {{theme_settings.container-border-global-color-base}};
    position: fixed;
    top: 0;
    width: 100%;
    height: 65px;
    z-index: 20;
    display: flex;
    align-items: center;
}

.amp-header-link {
    display: inline-block;
    text-decoration: none;
}

.amp-header-logo {
    flex-grow: 1;
    padding-right: 3.92857rem;
    text-align: center;
}

.amp-header-logo amp-img {
    margin: 0 auto;
}

.amp-header-logo img {
    object-fit: contain;
}

.amp-empty-space {
    width: 3.92857rem;
}

.amp-header-logo-text {
    display: block;
    font-family: "{{last (first (split theme_settings.headings-font '_') 2)}}", Arial, Helvetica, sans-serif;
    font-size: 20px;
    font-weight: 300;
    letter-spacing: .15714rem;
    margin-left: auto;
    margin-right: auto;
    overflow: hidden;
    padding: .21429rem 0;
    text-overflow: ellipsis;
    text-transform: uppercase;
    white-space: nowrap
}

.banners {
    background-color: {{theme_settings.global_banner_backgroundColor}};
    color: {{theme_settings/global_banner_foregroundColor}};
    padding: .78571rem 0;
    text-align: center
}
.banners p:last-child {
    margin-bottom: 0
}
.banners a {
    color: {{theme_settings.global_banner_backgroundColor}};
}
.banner {
    margin: 0 auto;
    max-width: 85.71429rem;
    width: 100%
}
.banner:before,
.banner:after {
    content: " ";
    display: table
}
.banner:after {
    clear: both
}
.banner + .banner {
    margin-top: 1.5rem
}


.form-fieldset {
    border-width: 0;
    margin: 0;
    padding: 0
}


.form-input {
    appearance: none;
    background-color: {{theme_settings.input-bg-color}};
    border-color: {{theme_settings.input-border-color}};
    border-style: solid;
    border-width: 1px;
    border-radius: 4px;
    color: {{theme_settings.input-font-color}};
    display: block;
    font-family: inherit;
    font-size: 1rem;
    font-smoothing: antialiased;
    height: 3rem;
    margin: 0;
    padding: .75rem 1rem;
    transition: border-color 100ms ease-out;
}
.form-input:disabled {
    background-color: {{theme_settings.input-disabled-bg}};
    cursor: not-allowed
}
.form-input[readonly] {
    background-color: {{theme_settings.input-disabled-bg}};
    cursor: text
}
.form-input:focus {
    border-color: {{theme_settings.input-border-color-active}};
    outline: none
}

.navPages {
    background-color: {{theme_settings.navPages-subMenu-backgroundColor}};
    height: 100%;
    overflow-y: auto;
    padding: 1.5rem
}

.navPages-list {
    margin: .78571rem 0 0;
    padding: 0;
    list-style: none;
}
.navPages-list + .navPages-list {
    border-top: 1px solid {{theme_settings.navPages-subMenu-separatorColor}};
    padding-top: .78571rem
}
.navPages-action {
    display: block;
    font-size: 15px;
    font-weight: 700;
    padding: .78571rem 0;
    text-decoration: none;
    text-transform: uppercase
}
