components/common/currency-selector:
{{#if currency_selector.currencies.length '>' 1}}
<li class="navUser-item currency-menu">
    <a class="navUser-action has-dropdown" href="#" data-dropdown="currencySelection" aria-controls="currencySelection" aria-expanded="false">{{{currency_selector.active_currency_flag}}} {{currency_selector.active_currency_code}} <i class="icon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i></a>
    <ul class="dropdown-menu" id="currencySelection" data-dropdown-content aria-hidden="true" tabindex="-1">
        {{#each currency_selector.currencies}}
        <li class="dropdown-menu-item">
            <a href="{{{switch_url}}}">
                {{#if is_active}}
                <strong>{{{flag}}} {{name}}</strong>
                {{else}}
                {{{flag}}} {{name}}
                {{/if}}
            </a>
        </li>
        {{/each}}
    </ul>
</li>
{{/if}}

components/common/header:
{{#unless (if template '===' 'pages/home')}}
    {{#if banners.top}}
        {{#if settings.data_tag_enabled}}
            <div class="banners" data-banner-location="top">
                {{#each (limit banners.top_metadata 1)}}
                    <div class="banner" data-event-type="promotion" data-entity-id="{{this.id}}" data-name="{{this.banner-name}}" data-position="{{this.location}}" data-name="{{this.banner-name}}">
                        <div data-event-type="promotion-click">
                            {{{this.content}}}
                        </div>
                    </div>
                {{/each}}
            </div>
        {{else}}
            <div class="banners" data-banner-location="top">
                <div class="banner">
                    {{{limit banners.top 1}}}
                </div>
            </div>
        {{/if}}
    {{/if}}
{{/unless}}

<header class="header {{#unless settings.store_logo.image}}header-logo-no-image{{/unless}} {{#if theme_settings.enable_simple_header}}{{#if theme_settings.enable_sticky_navigation}}fixed-header-simple{{/if}}{{/if}}" role="banner">
   {{#if theme_settings.show_global_banner}}
    {{/if}}  

    <div class="header-top position--{{theme_settings.logo-position}} {{#if theme_settings.enable_simple_header}}header-simple{{/if}}">
        {{> components/common/navigation}}

        <div class="header-logo header-logo--{{theme_settings.logo-position}} {{#if theme_settings.enable_sticky_navigation}}sticky {{#if theme_settings.hide_logo_on_sticky_navigation}}hide-on-sticky{{/if}}{{/if}}">
            {{> components/common/store-logo}}
        </div>

        {{#if theme_settings.enable_simple_header}}
            <div class="navPages-container" id="menu" data-menu>
                {{> components/common/navigation-menu}}
            </div>
        {{/if}}
    </div>


	<a href="#" class="mobileMenu-toggle" data-mobile-menu-toggle="menu{{#if theme_settings.enable_simple_header}}-mobile{{/if}}">
        <span class="mobileMenu-toggleIcon">Toggle menu</span>
    </a>

    {{{region name="header_bottom"}}}
    {{#unless theme_settings.enable_simple_header}}
        <div class="navPages-container {{#if theme_settings.enable_sticky_navigation}}sticky{{/if}}" id="menu" data-menu>
            {{> components/common/navigation-menu}}
        </div>
    {{else}}
        <div class="navPages-container navPages-mobile" id="menu-mobile" data-menu>
            {{> components/common/navigation-menu}}
        </div>
    {{/unless}}
</header>

components/f/navigation/navigation-list-:
{{#if children}}
    <a class="navPages-action navPages-action-depth-max has-subMenu is-root" href="{{url}}" data-collapsible="navPages-{{id}}">
        {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
    </a>
    {{> components/f/navigation/navigation-expanded-dropdown hide_view_all=hide_view_all}}
{{else}}
    <a class="navPages-action" href="{{url}}">{{name}}</a>
{{/if}}

{{#if children}}
<a class="navPages-action navPages-action-depth-max has-subMenu is-root" href="{{url}}" data-collapsible="navPages-{{id}}">
    {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
</a>
<div class="navPage-subMenu" id="navPages-{{id}}" aria-hidden="true" tabindex="-1">
    <ul class="navPage-subMenu-list">
        {{#unless hide_view_all}}
        <li class="navPage-subMenu-item">
            <a class="navPage-subMenu-action navPages-action" href="{{url}}">{{lang 'category.view_all.name' category=name}}</a>
        </li>
        {{/unless}}
        {{#each children}}
            <li class="navPage-subMenu-item">
                {{#if children}}
                    <a
                        class="navPage-subMenu-action navPages-action has-subMenu"
                        href="{{url}}"
                        data-collapsible="navPages-{{id}}"
                        data-collapsible-disabled-breakpoint="medium"
                        data-collapsible-disabled-state="open"
                        data-collapsible-enabled-state="closed">
                        {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
                    </a>
                    <ul class="navPage-childList" id="navPages-{{id}}">
                        {{#each children}}
                        <li class="navPage-childList-item">
                            <a class="navPage-childList-action navPages-action" href="{{url}}">{{name}}</a>
                        </li>
                        {{/each}}
                    </ul>
                {{else}}
                    <a class="navPage-subMenu-action navPages-action" href="{{url}}">{{name}}</a>
                {{/if}}
            </li>
        {{/each}}
    </ul>
</div>
{{else}}
<a class="navPages-action" href="{{url}}">{{name}}</a>
{{/if}}

{{#if children}}
    <a class="navPages-action navPages-action-depth-max has-subMenu is-root" href="{{url}}" data-collapsible="navPages-{{#if content}}p{{/if}}{{id}}">
        {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
    </a>
    {{> components/f/navigation/navigation-dropdown hide_view_all=hide_view_all content=content}}
{{else}}
    <a class="navPages-action" href="{{url}}">{{name}}</a>
{{/if}}
