components/common/currency-selector:
{{#if currency_selector.currencies.length '>' 1}}
<li class="navUser-item currency-menu">
    <a class="navUser-action has-dropdown" href="#" data-dropdown="currencySelection" aria-controls="currencySelection" aria-expanded="false">{{{currency_selector.active_currency_flag}}} {{currency_selector.active_currency_code}} <i class="icon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i></a>
    <ul class="dropdown-menu" id="currencySelection" data-dropdown-content aria-hidden="true" tabindex="-1">
        {{#each currency_selector.currencies}}
        <li class="dropdown-menu-item">
            <a href="{{{switch_url}}}">
                {{#if is_active}}
                <strong>{{{flag}}} {{name}}</strong>
                {{else}}
                {{{flag}}} {{name}}
                {{/if}}
            </a>
        </li>
        {{/each}}
    </ul>
</li>
{{/if}}

components/common/header:
{{#unless (if template '===' 'pages/home')}}
    {{#if banners.top}}
        {{#if settings.data_tag_enabled}}
            <div class="banners" data-banner-location="top">
                {{#each (limit banners.top_metadata 1)}}
                    <div class="banner" data-event-type="promotion" data-entity-id="{{this.id}}" data-name="{{this.banner-name}}" data-position="{{this.location}}" data-name="{{this.banner-name}}">
                        <div data-event-type="promotion-click">
                            {{{this.content}}}
                        </div>
                    </div>
                {{/each}}
            </div>
        {{else}}
            <div class="banners" data-banner-location="top">
                <div class="banner">
                    {{{limit banners.top 1}}}
                </div>
            </div>
        {{/if}}
    {{/if}}
{{/unless}}

<header class="header {{#unless settings.store_logo.image}}header-logo-no-image{{/unless}} {{#if theme_settings.enable_simple_header}}{{#if theme_settings.enable_sticky_navigation}}fixed-header-simple{{/if}}{{/if}}" role="banner">
   {{#if theme_settings.show_global_banner}}
    {{/if}}  

    <div class="header-top position--{{theme_settings.logo-position}} {{#if theme_settings.enable_simple_header}}header-simple{{/if}}">
        {{> components/common/navigation}}

        <div class="header-logo header-logo--{{theme_settings.logo-position}} {{#if theme_settings.enable_sticky_navigation}}sticky {{#if theme_settings.hide_logo_on_sticky_navigation}}hide-on-sticky{{/if}}{{/if}}">
            {{> components/common/store-logo}}
        </div>

        {{#if theme_settings.enable_simple_header}}
            <div class="navPages-container" id="menu" data-menu>
                {{> components/common/navigation-menu}}
            </div>
        {{/if}}
    </div>


	<a href="#" class="mobileMenu-toggle" data-mobile-menu-toggle="menu{{#if theme_settings.enable_simple_header}}-mobile{{/if}}">
        <span class="mobileMenu-toggleIcon">Toggle menu</span>
    </a>

    {{{region name="header_bottom"}}}
    {{#unless theme_settings.enable_simple_header}}
        <div class="navPages-container {{#if theme_settings.enable_sticky_navigation}}sticky{{/if}}" id="menu" data-menu>
            {{> components/common/navigation-menu}}
        </div>
    {{else}}
        <div class="navPages-container navPages-mobile" id="menu-mobile" data-menu>
            {{> components/common/navigation-menu}}
        </div>
    {{/unless}}
</header>

components/common/social-links:
{{#if social_media}}
    <ul class="socialLinks socialLinks--alt">
        {{#each social_media}}
            <li class="socialLinks-item">
                {{#if display_name '===' 'google+'}}
                    <a href="{{url}}" target="_blank">
                        <i class="icon icon--google"><svg><use xlink:href="#icon-google" /></svg></i>
                    </a>
                {{else}}
                    <a href="{{url}}" target="_blank">
                        <i class="icon icon--{{display_name}}"><svg><use xlink:href="#icon-{{display_name}}" /></svg></i>
                    </a>
                {{/if}}
            </li>
        {{/each}}
    </ul>
{{/if}}

components/f/footer/simple-footer:
{{#unless (if template '===' 'pages/home')}}
    {{#if banners.bottom}}
    <div class="banners banner-bottom" data-banner-location="bottom">
        <div class="banner">
            {{{limit banners.bottom 1}}}
        </div>
    </div>
    {{/if}}
{{/unless}}
<footer class="footer footer-simple" role="contentinfo">
    {{#if settings.show_newsletter_box}}
        <div class="footer-simple-newsletter"{{> components/f/animations/aos delay=400 offset=20}}>
            <div class="container">
                {{> components/common/subscription-form}}
            </div>
        </div>
    {{/if}}
    <div class="footer-simple-info">
        <div class="container footer-simple-navigation"{{> components/f/animations/aos delay=100 offset=20}}>
            <ul class="footer-info-list">
                {{#each pages}}
                    <li>
                        <a href="{{url}}">{{name}}</a>
                    </li>
                {{/each}}
                {{#and theme_settings.shop_by_brand_show_footer shop_by_brand.length}}
                    <li>
                        <a href="{{urls.brands}}">{{lang 'footer.brands'}}</a>
                    </li>
                {{/and}}
                <li>
                    <a href="{{urls.sitemap}}">{{lang 'sitemap.page_title'}}</a>
                </li>
            </ul>
        </div>
        {{#if theme_settings.social_icon_placement_bottom}}
            <h5 class="footer-info-heading">{{lang 'social.connect'}}</h5>
            {{> components/common/social-links}}
        {{/if}}
        {{#if theme_settings.show_payment_methods}}
            {{> components/f/footer/payment-icons}}
        {{/if}}
        {{#if theme_settings.geotrust_ssl_common_name}}
            <div class="footer-geotrust-ssl-seal">
                {{> components/common/geotrust-ssl-seal}}
            </div>
        {{/if}}
        {{#if settings.address}}
        <div class="footer-copyright">
            {{#if theme_settings.show_phone_number_in_header}}
                <div><a href="tel:{{settings.phone_number}}">{{lang 'header.need_help'}} {{settings.phone_number}}</a></div>
            {{/if}}
            <address>{{settings.address}}</address>
        </div>
        {{/if}}
        {{#if theme_settings.show_copyright_footer}}
            <div class="footer-copyright">
                <p class="powered-by">&copy; {{moment format="YYYY"}} {{settings.store_name}} </p>
            </div>
        {{/if}}
        {{#or theme_settings.show_designed_by theme_settings.show_powered_by}}
            <div class="footer-copyright">
                <p class="powered-by">
                    {{#if theme_settings.show_designed_by}}
                        <span>Designed by <a href="https://www.flairconsultancy.com?utm_source=store&amp;utm_medium=designedbyFlair" rel="nofollow" target="_blank">Flair</a>.</span>
                    {{/if}}
                    {{#if theme_settings.show_powered_by}}
                        <span>Powered by <a href="https://www.bigcommerce.com?utm_source=merchant&amp;utm_medium=poweredbyBC" rel="nofollow" target="_blank">BigCommerce</a>.</span>
                    {{/if}}
                </p>
            </div>
        {{/or}}
    </div>
</footer>
{{#if theme_settings.show_newsletter_popup}}
    {{> components/f/newsletter-popup/popup}}
{{/if}}

components/f/hero/storyboard-top:
{{#if theme_settings.hero_width '===' 'contain'}}<div class="container hero">{{/if}}
    <section class="heroStoryboard show--1">
        {{#each carousel.slides}}
        {{#if @index '===' 0}}
        <div class="heroCarousel-slide {{../../theme_settings.hero_align}} slide--{{@index}} {{#if ../../theme_settings.homepage_stretch_carousel_images}}heroCarousel-slide--stretch{{/if}}">
            <a href="{{url}}">
                <img class="heroCarousel-image lazyload" src="{{cdn 'img/loading.svg'}}" data-src="{{image}}" alt="{{alt_text}}" title="{{alt_text}}"
                {{#if image_width}}width="{{image_width}}"{{/if}} {{#if image_height}}height="{{image_height}}"{{/if}} />
                {{#if heading}}
                    {{> components/f/hero/carousel-content show_background=true theme_settings=../../../theme_settings}}
                {{else}}
                    {{#if text}}
                        {{> components/f/hero/carousel-content show_background=true theme_settings=../../../../theme_settings}}
                    {{else}}
                        {{#if button_text}}
                            {{> components/f/hero/carousel-content show_background=false theme_settings=../../../../../theme_settings}}
                        {{/if}}
                    {{/if}}
                {{/if}}
            </a>
        </div>
        {{/if}}
        {{/each}}
    </section>
{{#if theme_settings.hero_width '===' 'contain'}}</div>{{/if}}

components/f/navigation/navigation-list-:
{{#if children}}
    <a class="navPages-action navPages-action-depth-max has-subMenu is-root" href="{{url}}" data-collapsible="navPages-{{id}}">
        {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
    </a>
    {{> components/f/navigation/navigation-expanded-dropdown hide_view_all=hide_view_all}}
{{else}}
    <a class="navPages-action" href="{{url}}">{{name}}</a>
{{/if}}

{{#if children}}
<a class="navPages-action navPages-action-depth-max has-subMenu is-root" href="{{url}}" data-collapsible="navPages-{{id}}">
    {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
</a>
<div class="navPage-subMenu" id="navPages-{{id}}" aria-hidden="true" tabindex="-1">
    <ul class="navPage-subMenu-list">
        {{#unless hide_view_all}}
        <li class="navPage-subMenu-item">
            <a class="navPage-subMenu-action navPages-action" href="{{url}}">{{lang 'category.view_all.name' category=name}}</a>
        </li>
        {{/unless}}
        {{#each children}}
            <li class="navPage-subMenu-item">
                {{#if children}}
                    <a
                        class="navPage-subMenu-action navPages-action has-subMenu"
                        href="{{url}}"
                        data-collapsible="navPages-{{id}}"
                        data-collapsible-disabled-breakpoint="medium"
                        data-collapsible-disabled-state="open"
                        data-collapsible-enabled-state="closed">
                        {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
                    </a>
                    <ul class="navPage-childList" id="navPages-{{id}}">
                        {{#each children}}
                        <li class="navPage-childList-item">
                            <a class="navPage-childList-action navPages-action" href="{{url}}">{{name}}</a>
                        </li>
                        {{/each}}
                    </ul>
                {{else}}
                    <a class="navPage-subMenu-action navPages-action" href="{{url}}">{{name}}</a>
                {{/if}}
            </li>
        {{/each}}
    </ul>
</div>
{{else}}
<a class="navPages-action" href="{{url}}">{{name}}</a>
{{/if}}

{{#if children}}
    <a class="navPages-action navPages-action-depth-max has-subMenu is-root" href="{{url}}" data-collapsible="navPages-{{#if content}}p{{/if}}{{id}}">
        {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
    </a>
    {{> components/f/navigation/navigation-dropdown hide_view_all=hide_view_all content=content}}
{{else}}
    <a class="navPages-action" href="{{url}}">{{name}}</a>
{{/if}}

components/f/newsletter-popup/popup:
{{inject "enableNewsletterPopup" theme_settings.show_newsletter_popup}}
{{inject "newsletterPopupDelay" theme_settings.newsletter_popup_delay}}
{{inject "reappearAfter" theme_settings.newsletter_popup_reappear_after}}
{{inject "pageType" page_type}}
<div class="newsletterPopup-overlay">
    <div class="newsletterPopup">
        <a href="#" class="newsletterPopup-dismiss modal-close" aria-label="{{lang 'common.close'}}" role="button">
            <span aria-hidden="true">&#215;</span>
        </a>
        {{#if theme_settings.newsletter_popup_image}}
            <img class="newsletterPopup-image" src="{{cdn 'webdav:img/'}}{{theme_settings.newsletter_popup_image}}">
        {{/if}}
        <h2 class="newsletterPopup-title">
            {{#if theme_settings.newsletter_popup_title}}
                {{theme_settings.newsletter_popup_title}}
            {{else}}
                {{lang 'newsletter.subscribe'}}
            {{/if}}
        </h2>
        <p class="newsletterPopup-intro">
            {{#if theme_settings.newsletter_popup_intro}}
                {{theme_settings.newsletter_popup_intro}}
            {{else}}
                {{lang 'newsletter.subscribe_intro'}}
            {{/if}}
        </p>
        <form class="form" action="{{urls.subscribe.action}}" method="post">
            <fieldset class="form-fieldset">
                <input type="hidden" name="action" value="subscribe">
                <input type="hidden" name="nl_first_name" value="bc">
                <input type="hidden" name="check" value="1">
                <div class="form-field">
                    <label class="form-label is-srOnly" for="nl_email">{{lang 'common.email_address'}}</label>
                    <div class="form-prefixPostfix wrap">
                        <input class="form-input" name="nl_email" type="email" value="{{customer.email}}" placeholder="{{lang 'newsletter.email_placeholder'}}">
                        <input class="button button--primary form-prefixPostfix-button--postfix" type="submit" value="{{lang 'newsletter.subscribe_submit'}}">
                    </div>
                    {{#if settings.show_newsletter_summary }}
                        <div class="newsletter-summary">{{settings.newsletter_summary}}</div>
                    {{/if}}
                </div>
            </fieldset>
        </form>
        <a class="newsletterPopup-dismiss newsletterPopup-dismiss--text" href="#">{{lang 'newsletter.popup_dismiss'}}</a>
    </div>
</div>

pages/home:
---
products:
    new:
        limit: {{theme_settings.homepage_new_products_count}}
    featured:
        limit: {{theme_settings.homepage_featured_products_count}}
    top_sellers:
        limit: {{theme_settings.homepage_top_products_count}}
carousel: {{theme_settings.homepage_show_carousel}}
blog:
    recent_posts:
        limit: {{theme_settings.homepage_blog_posts_count}}
---
{{inject "enableSwatches" theme_settings.show_swatches_on_grid}}
{{inject "enableSizes" theme_settings.show_sizes_on_grid}}
{{inject "enableImageHoverSwitcher" theme_settings.enable_image_hover_switcher}}

{{#partial "hero"}}
    {{#if carousel}}
        {{#or (if theme_settings.carousel-type '===' 'storyboard') theme_settings.conversion_optimization_mode}}
            {{> components/f/hero/storyboard-top}}
        {{else}}
            {{> components/f/hero/carousel arrows=theme_settings.homepage_show_carousel_arrows}}
        {{/or}}
    {{/if}}
    {{{snippet 'home_content'}}}
{{/partial}}

{{#partial "page"}}

    {{#each shipping_messages}}
        {{> components/common/alert-info message}}
    {{/each}}

<div class="main full">

     {{> components/common/custom-section-mattresses}}
    <div class="blog-homepage">
        {{> components/f/blog/home-articles-list posts=blog.recent_posts columns=theme_settings.homepage_blog_posts_column_count theme_settings=theme_settings}}
    </div>


     {{> components/common/custom-section-test-mattresses}}

</div>

{{/partial}}
{{> layout/custom-home-base}}
